import React from 'react';
import { Link } from 'react-router-dom';

const mockOrders = [
  {
    id: '1',
    date: '2024-03-15',
    restaurant: 'Pizza Bakers',
    items: [
      { name: 'Margherita Pizza', quantity: 1, price: 299 },
      { name: 'Garlic Bread', quantity: 2, price: 149 }
    ],
    status: 'Delivered',
    total: 597,
    paymentMethod: 'Online Payment'
  },
  {
    id: '2',
    date: '2024-03-14',
    restaurant: 'Burger Farm',
    items: [
      { name: 'Classic Burger', quantity: 2, price: 199 },
      { name: 'French Fries', quantity: 1, price: 99 }
    ],
    status: 'Delivered',
    total: 497,
    paymentMethod: 'Cash on Delivery'
  }
];

const OrderHistory = () => {
  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Your Orders</h1>

      <div className="space-y-6">
        {mockOrders.map((order) => (
          <div key={order.id} className="bg-white rounded-lg shadow-md p-6">
            <div className="flex justify-between items-start mb-4">
              <div>
                <h2 className="text-xl font-semibold mb-2">{order.restaurant}</h2>
                <p className="text-gray-600">Order #{order.id}</p>
                <p className="text-gray-600">Placed on {new Date(order.date).toLocaleDateString()}</p>
              </div>
              <div className="text-right">
                <span className="inline-block px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm font-medium">
                  {order.status}
                </span>
                <p className="mt-2 text-gray-600">{order.paymentMethod}</p>
              </div>
            </div>

            <div className="border-t border-gray-200 pt-4">
              <h3 className="font-semibold mb-2">Order Items:</h3>
              <div className="space-y-2">
                {order.items.map((item, index) => (
                  <div key={index} className="flex justify-between text-gray-600">
                    <span>{item.quantity}x {item.name}</span>
                    <span>₹{item.price * item.quantity}</span>
                  </div>
                ))}
              </div>

              <div className="border-t border-gray-200 mt-4 pt-4">
                <div className="flex justify-between font-semibold">
                  <span>Total Amount</span>
                  <span>₹{order.total}</span>
                </div>
              </div>
            </div>

            <div className="mt-6 flex justify-end space-x-4">
              <button className="text-orange-500 hover:text-orange-600 transition">
                Reorder
              </button>
              <Link 
                to={`/track-order/${order.id}`}
                className="text-orange-500 hover:text-orange-600 transition"
              >
                Track Order
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrderHistory;
