import React from "react";
import "./PizzaBakers.css";
import { useCart } from '../context/CartContext';

const RESTAURANT_NAME = "Chatkara";

const menuItems = [
  { id: "ch1", name: "Masala Chaap", price: 160 },
  { id: "ch2", name: "Punjabi Chaap", price: 160 },
  { id: "ch3", name: "Achari Chaap", price: 160 },
  { id: "ch4", name: "Tandoori Chaap", price: 170 },
  { id: "ch5", name: "Malai Chaap", price: 170 },
  { id: "ch6", name: "Afghani Chaap", price: 170 },
];

const Chatkara = () => {
  const { addToCart } = useCart();

  const handleAddToCart = (item) => {
    addToCart(item, RESTAURANT_NAME);
  };

  return (
    <div className="restaurant-page">
      <div className="header">
        <img src="/logos/chatkara.png" alt="Chatkara" className="restaurant-logo" />
        <div>
          <h1>Chatkara</h1>
          <p>Delicious chaaps and tikkas made fresh for you!</p>
        </div>
      </div>

      <div className="menu">
        <h2>Menu</h2>
        {menuItems.map((item) => (
          <div key={item.id} className="menu-item">
            <div className="menu-item-info">
              <h3>{item.name}</h3>
              <p>â‚¹{item.price}</p>
            </div>
            <button 
              className="add-to-cart-button"
              onClick={() => handleAddToCart(item)}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Chatkara;
