import React from "react";
import "./PizzaBakers.css";
import { useCart } from '../context/CartContext';

const RESTAURANT_NAME = "Burger Farm";

const menuItems = [
  { id: "bf1", name: "Veg Burger", price: 109 },
  { id: "bf2", name: "Go Green Burger", price: 109 },
  { id: "bf3", name: "Devil's Chicken Delight", price: 139 },
  { id: "bf4", name: "Farm Grilled Chicken", price: 149 },
  { id: "bf5", name: "Farm Signature Meal", price: 159 },
];

const BurgerFarm = () => {
  const { addToCart } = useCart();

  const handleAddToCart = (item) => {
    addToCart(item, RESTAURANT_NAME);
  };

  return (
    <div className="restaurant-page">
      <div className="header">
        <img src="/logos/burger-farm.png" alt="Burger Farm" className="restaurant-logo" />
        <div>
          <h1>Burger Farm</h1>
          <p>Delicious and freshly made burgers just for you!</p>
        </div>
      </div>

      <div className="menu">
        <h2>Menu</h2>
        {menuItems.map((item) => (
          <div key={item.id} className="menu-item">
            <div className="menu-item-info">
              <h3>{item.name}</h3>
              <p>â‚¹{item.price}</p>
            </div>
            <button 
              className="add-to-cart-button"
              onClick={() => handleAddToCart(item)}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BurgerFarm;
