import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const PaymentOptions = ({ amount, onClose }) => {
  const navigate = useNavigate();
  const { checkout } = useCart();

  const handleCashPayment = async () => {
    try {
      // Create order with cash payment
      const success = await checkout({
        paymentMethod: 'cash',
        deliveryAddress: 'User Address' // You might want to get this from a form or user profile
      });

      if (success) {
        // Navigate to orders page after successful order
        navigate('/orders', { state: { fromCheckout: true } });
      }
    } catch (error) {
      alert('Failed to place order. Please try again.');
    }
  };

  const handleOnlinePayment = () => {
    // Initialize Razorpay payment
    const options = {
      key: 'YOUR_RAZORPAY_KEY_ID',
      amount: amount * 100,
      currency: 'INR',
      name: 'Hostel Craves',
      description: 'Food Order Payment',
      handler: async function(response) {
        try {
          // Create order with online payment
          const success = await checkout({
            paymentMethod: 'online',
            paymentDetails: {
              transactionId: response.razorpay_payment_id,
              paidAt: new Date()
            },
            deliveryAddress: 'User Address' // You might want to get this from a form or user profile
          });

          if (success) {
            // Navigate to orders page after successful order
            navigate('/orders', { state: { fromCheckout: true } });
          }
        } catch (error) {
          alert('Failed to place order. Please try again.');
        }
      },
      prefill: {
        name: '',
        email: '',
        contact: ''
      },
      theme: {
        color: '#f97316'
      }
    };

    const razorpayInstance = new window.Razorpay(options);
    razorpayInstance.open();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-white p-6 rounded-lg shadow-xl w-96">
        <h2 className="text-2xl font-bold mb-4">Choose Payment Method</h2>
        <div className="space-y-4">
          <button
            onClick={handleCashPayment}
            className="w-full bg-gray-800 text-white py-3 rounded-lg hover:bg-gray-900 transition"
          >
            Pay with Cash
          </button>
          <button
            onClick={handleOnlinePayment}
            className="w-full bg-orange-500 text-white py-3 rounded-lg hover:bg-orange-600 transition"
          >
            Pay Online with Razorpay
          </button>
          <button
            onClick={onClose}
            className="w-full border border-gray-300 py-3 rounded-lg hover:bg-gray-50 transition"
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default PaymentOptions;
