import React from "react";
import "./PizzaBakers.css";
import { useCart } from '../context/CartContext';

const RESTAURANT_NAME = "Pizza Bakers";

const menuItems = [
  { id: "pb1", name: "Margherita Pizza", price: 199, category: "veg" },
  { id: "pb2", name: "Farmhouse Pizza", price: 299, category: "veg" },
  { id: "pb3", name: "Veggie Paradise", price: 279, category: "veg" },
  { id: "pb4", name: "Peppy Paneer Pizza", price: 299, category: "veg" },
  { id: "pb5", name: "Chicken Dominator", price: 359, category: "non-veg" },
  { id: "pb6", name: "Pepper BBQ Chicken", price: 329, category: "non-veg" },
  { id: "pb7", name: "Chicken Sausage Pizza", price: 299, category: "non-veg" },
  { id: "pb8", name: "Non-Veg Supreme", price: 379, category: "non-veg" }
];

const PizzaBakers = () => {
  const { addToCart } = useCart();

  const handleAddToCart = (item) => {
    addToCart(item, RESTAURANT_NAME);
  };

  // Separate veg and non-veg items
  const vegItems = menuItems.filter(item => item.category === "veg");
  const nonVegItems = menuItems.filter(item => item.category === "non-veg");

  return (
    <div className="restaurant-page">
      <div className="header">
        <img src="/logos/pizza-bakers.png" alt="Pizza Bakers" className="restaurant-logo" />
        <div>
          <h1>Pizza Bakers</h1>
          <p>Enjoy the finest pizza with a variety of vegetarian and non-vegetarian options</p>
        </div>
      </div>

      <div className="menu">
        <h2>Veg Pizzas</h2>
        {vegItems.map((item) => (
          <div key={item.id} className="menu-item">
            <div className="menu-item-info">
              <h3>{item.name}</h3>
              <p>₹{item.price}</p>
            </div>
            <button 
              className="add-to-cart-button" 
              onClick={() => handleAddToCart(item)}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      <div className="menu">
        <h2>Non-Veg Pizzas</h2>
        {nonVegItems.map((item) => (
          <div key={item.id} className="menu-item">
            <div className="menu-item-info">
              <h3>{item.name}</h3>
              <p>₹{item.price}</p>
            </div>
            <button 
              className="add-to-cart-button" 
              onClick={() => handleAddToCart(item)}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PizzaBakers;
