import React, { useState, useEffect } from "react";
import "./PizzaBakers.css";
import { useCart } from '../context/CartContext';
import { getRestaurantMenu } from '../services/api';

const RESTAURANT_NAME = "Pizza Bakers";
const RESTAURANT_ID = "67f65b622c3ce3aff729dd8c"; // ID from our seeded data

const PizzaBakers = () => {
  const { addToCart } = useCart();
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMenu = async () => {
      try {
        const data = await getRestaurantMenu(RESTAURANT_ID);
        setMenuItems(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchMenu();
  }, []);

  const handleAddToCart = (item) => {
    addToCart(item, RESTAURANT_NAME);
  };

  if (loading) return <div>Loading menu...</div>;
  if (error) return <div>Error: {error}</div>;

  // Separate veg and non-veg items
  const vegItems = menuItems.filter(item => item.category === "veg");
  const nonVegItems = menuItems.filter(item => item.category === "non-veg");

  return (
    <div className="restaurant-page">
      <div className="header">
        <img src="/logos/pizza-bakers.png" alt="Pizza Bakers" className="restaurant-logo" />
        <div>
          <h1>Pizza Bakers</h1>
          <p>Enjoy the finest pizza with a variety of vegetarian and non-vegetarian options</p>
        </div>
      </div>

      <div className="menu">
        <h2>Veg Pizzas</h2>
        {vegItems.map((item) => (
          <div key={item.id} className="menu-item">
            <div className="menu-item-info">
              <h3>{item.name}</h3>
              <p>₹{item.price}</p>
            </div>
            <button 
              className="add-to-cart-button" 
              onClick={() => handleAddToCart(item)}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      <div className="menu">
        <h2>Non-Veg Pizzas</h2>
        {nonVegItems.map((item) => (
          <div key={item.id} className="menu-item">
            <div className="menu-item-info">
              <h3>{item.name}</h3>
              <p>₹{item.price}</p>
            </div>
            <button 
              className="add-to-cart-button" 
              onClick={() => handleAddToCart(item)}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PizzaBakers;
