import React, { createContext, useContext, useState } from 'react';
import { createOrder } from '../services/api';

const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [currentRestaurant, setCurrentRestaurant] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Add item to cart
  const addToCart = (item, restaurantName) => {
    if (cartItems.length === 0) {
      // If cart is empty, set the current restaurant and add item
      setCurrentRestaurant(restaurantName);
      setCartItems([{ ...item, quantity: 1, restaurant: restaurantName }]);
    } else if (currentRestaurant !== restaurantName) {
      // If item is from a different restaurant, show confirmation
      if (window.confirm(
        `Your cart contains items from ${currentRestaurant}. Would you like to clear your cart and add items from ${restaurantName} instead?`
      )) {
        setCurrentRestaurant(restaurantName);
        setCartItems([{ ...item, quantity: 1, restaurant: restaurantName }]);
      }
    } else {
      // Item is from the same restaurant
      setCartItems(prevItems => {
        const existingItem = prevItems.find(i => i.id === item.id);
        if (existingItem) {
          return prevItems.map(i =>
            i.id === item.id
              ? { ...i, quantity: i.quantity + 1 }
              : i
          );
        }
        return [...prevItems, { ...item, quantity: 1, restaurant: restaurantName }];
      });
    }
  };

  // Remove item from cart
  const removeFromCart = (itemId) => {
    setCartItems(prevItems => {
      const updatedItems = prevItems.filter(item => item.id !== itemId);
      if (updatedItems.length === 0) {
        setCurrentRestaurant(null);
      }
      return updatedItems;
    });
  };

  // Update item quantity
  const updateQuantity = (itemId, newQuantity) => {
    if (newQuantity < 1) {
      removeFromCart(itemId);
      return;
    }
    setCartItems(prevItems =>
      prevItems.map(item =>
        item.id === itemId
          ? { ...item, quantity: newQuantity }
          : item
      )
    );
  };

  // Calculate total amount
  const getTotalAmount = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  // Get cart items count
  const getCartCount = () => {
    return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  // Get current restaurant name
  const getCurrentRestaurant = () => currentRestaurant;

  // Create order in backend
  const checkout = async (checkoutData) => {
    try {
      setLoading(true);
      setError(null);

      const orderData = {
        restaurant: currentRestaurant,
        items: cartItems.map(item => ({
          name: item.name,
          price: item.price,
          quantity: item.quantity
        })),
        totalAmount: getTotalAmount(),
        paymentMethod: checkoutData.paymentMethod,
        deliveryAddress: checkoutData.deliveryAddress
      };

      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Please login to place an order');
      }

      await createOrder(orderData, token);

      // Clear cart after successful order
      setCartItems([]);
      setCurrentRestaurant(null);
      setLoading(false);

      return true;
    } catch (err) {
      console.error('Checkout error:', err);
      setError(err.message);
      setLoading(false);
      return false;
    }
  };

  const value = {
    cartItems,
    addToCart,
    removeFromCart,
    updateQuantity,
    getTotalAmount,
    getCartCount,
    getCurrentRestaurant,
    checkout,
    loading,
    error
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
