import React from "react";
import "./PizzaBakers.css";
import { useCart } from '../context/CartContext';

const RESTAURANT_NAME = "NBC";

const menuItems = [
  { id: "nbc1", name: "Strawberry Shake", price: 182 },
  { id: "nbc2", name: "Black Current Shake", price: 182 },
  { id: "nbc3", name: "Chocolate Shake", price: 182 },
  { id: "nbc4", name: "Butterscotch Shake", price: 182 },
  { id: "nbc5", name: "Oreo Shake", price: 196 },
  { id: "nbc6", name: "KitKat Shake", price: 210 },
  { id: "nbc7", name: "Red Velvet Shake", price: 224 },
  { id: "nbc8", name: "Nutella Shake", price: 224 },
  { id: "nbc9", name: "Brownie Shake", price: 224 },
  { id: "nbc10", name: "Belgium Crunchy Shake", price: 224 },
  { id: "nbc11", name: "Blueberry Shake", price: 252 },
  { id: "nbc12", name: "Rocher Shake", price: 280 },
  { id: "nbc13", name: "Icy Blue", price: 140 },
  { id: "nbc14", name: "Litchi Slush", price: 140 },
  { id: "nbc15", name: "Galaxy Mocktail", price: 182 },
  { id: "nbc16", name: "Plain Cheese Maggie", price: 140 },
  { id: "nbc17", name: "Tandoori Maggie", price: 154 },
  { id: "nbc18", name: "Peri Peri Masala", price: 168 },
  { id: "nbc19", name: "Cookies", price: 56 },
  { id: "nbc20", name: "Jumbo Muffin", price: 140 },
  { id: "nbc21", name: "Brownie", price: 140 },
  { id: "nbc22", name: "Brownie Shot", price: 168 },
  { id: "nbc23", name: "Mud Pie Brownie", price: 168 },
];

const NBC = () => {
  const { addToCart } = useCart();

  const handleAddToCart = (item) => {
    addToCart(item, RESTAURANT_NAME);
  };

  return (
    <div className="restaurant-page">
      <div className="header">
        <img src="/images/nbc.png" alt="NBC" className="restaurant-logo" />
        <div>
          <h1>NBC</h1>
          <p>Delicious shakes, desserts, and snacks to satisfy your cravings!</p>
        </div>
      </div>

      <div className="menu">
        <h2>Menu</h2>
        {menuItems.map((item) => (
          <div key={item.id} className="menu-item">
            <div className="menu-item-info">
              <h3>{item.name}</h3>
              <p>â‚¹{item.price}</p>
            </div>
            <button 
              className="add-to-cart-button"
              onClick={() => handleAddToCart(item)}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default NBC;
